import React from 'react';
import ContactForm from '../components/ContactForm';

const Contact = () => (
  <div className="container mx-auto p-6">
    <h1 className="text-2xl font-bold mb-4 font-heading">Contact Us</h1>
    {/* Google Map */}
    <div className="mb-8 w-full h-64 rounded-2xl overflow-hidden">
      <iframe
        src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3151.8354345093747!2d144.96305791531977!3d-37.816279142021214!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x6ad65d43e3b545bf%3A0x5045675218ce840!2sClearview%20Optical!5e0!3m2!1sen!2sus!4v1628792345678"
        width="100%"
        height="100%"
        style={{ border: 0 }}
        allowFullScreen=""
        loading="lazy"
      ></iframe>
    </div>
    {/* Contact Form */}
    <ContactForm />
    {/* Contact Details */}
    <div className="mt-8 text-center space-y-2">
      <a href="tel:1234567890" className="block text-primary hover:underline">Call us: (123) 456-7890</a>
      <a href="mailto:info@clearview.com" className="block text-primary hover:underline">Email: info@clearview.com</a>
      <a href="https://wa.me/1234567890" target="_blank" rel="noopener noreferrer" className="block text-primary hover:underline">WhatsApp Chat</a>
    </div>
  </div>
);

export default Contact;
