import React from 'react';
import { Link, NavLink } from 'react-router-dom';

const Navbar = () => {
  return (
    <nav className="bg-primary text-white p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">ClearView</Link>
        <ul className="flex space-x-4">
          <li><NavLink to="/" className={({ isActive }) => isActive ? 'underline' : ''}>Home</NavLink></li>
          <li><NavLink to="/products" className={({ isActive }) => isActive ? 'underline' : ''}>Shop</NavLink></li>
          <li><NavLink to="/book" className={({ isActive }) => isActive ? 'underline' : ''}>Appointment</NavLink></li>
          <li><NavLink to="/contact" className={({ isActive }) => isActive ? 'underline' : ''}>Contact</NavLink></li>
        </ul>
        <ul className="flex space-x-4">
          <li><NavLink to="/login" className={({ isActive }) => isActive ? 'underline' : ''}>Login</NavLink></li>
          <li><NavLink to="/signup" className={({ isActive }) => isActive ? 'underline' : ''}>Signup</NavLink></li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
