import React from 'react';
import products from '../data/products';
import ProductCard from '../components/ProductCard';
import AppointmentForm from '../components/AppointmentForm';

const Home = () => {
  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 font-heading">Welcome to ClearView Optical</h1>
      {/* Hero Section */}
      <div className="bg-primary text-white rounded-2xl p-8 mb-8">
        <h2 className="text-2xl font-bold mb-4">Your Vision, Our Precision</h2>
        <p className="mb-4">Explore premium eyewear and book your comprehensive eye test today.</p>
        <a href="/products" className="bg-accent text-white py-2 px-4 rounded-lg hover:bg-opacity-90 transition">Shop Now</a>
      </div>
      {/* Featured Products */}
      <section className="mb-12">
        <h2 className="text-2xl font-bold mb-4 font-heading">Featured Products</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {products.slice(0, 4).map((p) => (
            <ProductCard key={p.id} product={p} />
          ))}
        </div>
      </section>
      {/* Appointment Section */}
      <section id="appointment">
        <h2 className="text-2xl font-bold mb-4 font-heading">Book an Appointment</h2>
        <AppointmentForm />
      </section>
    </div>
  );
};

export default Home;
