import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className={`sticky top-0 z-50 transition-all duration-300 ${
      scrolled ? 'bg-white shadow-soft py-2' : 'bg-background-light bg-opacity-70 backdrop-blur-sm py-4'
    }`}>
      <div className="container mx-auto flex justify-between items-center px-4">
        <Link to="/" className="text-2xl font-bold text-primary-500 hover:text-primary-600 transition-colors">
          <span className="flex items-center">
            ClearView
            <span className="text-accent-400 ml-1">Optical</span>
          </span>
        </Link>
        <ul className="hidden md:flex space-x-6">
          <li><NavLink to="/" className={({ isActive }) => 
            isActive ? 'text-primary-600 font-semibold border-b-2 border-accent-400 pb-1' : 
            'text-neutral-600 hover:text-primary-500 transition-colors'}>Home</NavLink></li>
          <li><NavLink to="/products" className={({ isActive }) => 
            isActive ? 'text-primary-600 font-semibold border-b-2 border-accent-400 pb-1' : 
            'text-neutral-600 hover:text-primary-500 transition-colors'}>Shop</NavLink></li>
          <li><NavLink to="/book" className={({ isActive }) => 
            isActive ? 'text-primary-600 font-semibold border-b-2 border-accent-400 pb-1' : 
            'text-neutral-600 hover:text-primary-500 transition-colors'}>Appointment</NavLink></li>
          <li><NavLink to="/contact" className={({ isActive }) => 
            isActive ? 'text-primary-600 font-semibold border-b-2 border-accent-400 pb-1' : 
            'text-neutral-600 hover:text-primary-500 transition-colors'}>Contact</NavLink></li>
        </ul>
        <div className="flex items-center space-x-4">
          <NavLink to="/login" className="hidden md:block px-4 py-2 text-neutral-700 hover:text-primary-600 transition-colors">Login</NavLink>
          <NavLink to="/signup" className="hidden md:block px-4 py-2 rounded-full bg-primary-500 text-white hover:bg-primary-600 transition-colors shadow-soft">Signup</NavLink>
          
          {/* Mobile menu button */}
          <button className="md:hidden text-primary-500">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="w-6 h-6">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
