import React from 'react';

const ProductCard = ({ product }) => (
  <div className="bg-white shadow-md rounded-2xl overflow-hidden p-4 hover:shadow-lg transition-shadow">
    <img
      src={product.image}
      alt={product.name}
      className="w-full h-48 object-cover rounded-lg mb-4"
    />
    <h3 className="text-lg font-bold font-heading mb-2">{product.name}</h3>
    <p className="text-gray-700 font-body mb-2">{product.price}</p>
    <button className="bg-primary text-white py-2 px-4 rounded-lg hover:bg-opacity-90 transition">Add to Cart</button>
  </div>
);

export default ProductCard;
