import React, { useState } from 'react';

const ContactForm = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Message sent successfully!');
    setFormData({ name: '', email: '', message: '' });
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-6 rounded-2xl shadow-md">
      <div className="mb-4">
        <label className="block mb-1">Name</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required className="w-full border p-2 rounded" />
      </div>
      <div className="mb-4">
        <label className="block mb-1">Email</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required className="w-full border p-2 rounded" />
      </div>
      <div className="mb-4">
        <label className="block mb-1">Message</label>
        <textarea name="message" value={formData.message} onChange={handleChange} required className="w-full border p-2 rounded h-24" />
      </div>
      <button type="submit" className="w-full bg-primary text-white py-2 rounded-lg hover:bg-opacity-90 transition">
        Send Message
      </button>
    </form>
  );
};

export default ContactForm;
