import React, { useState } from 'react';

const AppointmentForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    concern: '',
    date: '',
    time: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Appointment booked successfully!');
    setFormData({ name: '', email: '', phone: '', concern: '', date: '', time: '' });
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-6 rounded-2xl shadow-md">
      <div className="mb-4">
        <label className="block mb-1">Name</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required className="w-full border p-2 rounded" />
      </div>
      <div className="mb-4">
        <label className="block mb-1">Email</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required className="w-full border p-2 rounded" />
      </div>
      <div className="mb-4">
        <label className="block mb-1">Phone</label>
        <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required className="w-full border p-2 rounded" />
      </div>
      <div className="mb-4">
        <label className="block mb-1">Eye Concern</label>
        <textarea name="concern" value={formData.concern} onChange={handleChange} className="w-full border p-2 rounded" />
      </div>
      <div className="mb-4">
        <label className="block mb-1">Date</label>
        <input type="date" name="date" value={formData.date} onChange={handleChange} required className="w-full border p-2 rounded" />
      </div>
      <div className="mb-4">
        <label className="block mb-1">Time</label>
        <input type="time" name="time" value={formData.time} onChange={handleChange} required className="w-full border p-2 rounded" />
      </div>
      <button type="submit" className="w-full bg-primary text-white py-2 rounded-lg hover:bg-opacity-90 transition">
        Book Appointment
      </button>
    </form>
  );
};

export default AppointmentForm;
